import style from './SearchBar.module.css'
import { useState } from 'react'

const SearchBar = ({onSearch, randomSearch}) => {
   const [id, setId]= useState('')

   const handleChange = (event) => {
      setId(event.target.value)
   }

   const [randomId, setRandomId]= useState(1)

   const randomNum = (min=1 , max=826) => {
      let resultado = Math.floor((Math.random() * (max - min + 1)) + min)
      setRandomId(randomId + resultado)
      // let aux = []
      // let random = (Math.floor((Math.random() * (max - min + 1)) + min))
      // if (aux.contains(random) !== true){
      //    aux.push(random)
      //    setRandomId(random)
      // }
      // else{
      //    randomNum(min=1 , max=826, aux)
      // }
   }

   return (
      <div className={style.searchBar}>
         <input className={style.search} type='search' onChange={handleChange} value={id}/>
         <button className={style.searchButton} onClick={() =>{onSearch(id)}}>Agregar</button>
         <button className={style.randomButton} onClick={() =>{randomSearch(randomId)}}>Random</button>
      </div>
   );
}

export default SearchBar
