export default (data) =>{
    if (!data.email){
        errors.e1 = 'Ingrese Email!'
    }
    if (!data.email.includes('@')){
        errors.e2 = 'Email Invalido!'
    }
    if (!data.email.includes('.')){
        errors.e3 = 'Email Invalido!'
    }
    if (!/\d/.test(data.password)){
        errors.p1 = 'Al menos debe tener un numero!'
    }
    if (data.password < 6 || data.password > 15){
        errors.p2 = 'La contraseña debe tener entre 6 y 15 caracteres'
    }
    return errors;
}
